import React, { useState } from 'react';
import '../styles/Login.css';
import { useAppContext } from '../context/AppContext';

const Login = () => {
  const { login } = useAppContext();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('patient');
  const [name, setName] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // In a real application, implement proper authentication here
    // For now, simulate a logged in user. We mark registration for known roles.
    const registered = userType !== 'patient' ? true : true; // treat selection as registered for simplicity
    const user = { role: userType, name: name || (userType === 'doctor' ? 'Dr. Example' : 'Patient Example'), email, registered };
    login(user);
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>MedConnect Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>User Type</label>
            <select
              value={userType}
              onChange={(e) => setUserType(e.target.value)}
              className="form-control"
            >
              <option value="patient">Patient</option>
              <option value="doctor">Doctor</option>
              <option value="admin">Admin</option>
              <option value="pharmacist">Pharmacist</option>
            </select>
          </div>
          <div className="form-group">
            <label>Name</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="form-control"
              required
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="form-control"
              required
            />
          </div>
          <button type="submit" className="login-button">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
