import React, { createContext, useContext, useState } from 'react';

const AppContext = createContext(null);

export const AppProvider = ({ children }) => {
  const [users, setUsers] = useState([
    { id: 1, name: 'Dr. Smith', email: 'dr.smith@example.com', role: 'doctor', status: 'active' },
    { id: 2, name: 'John Doe', email: 'john@example.com', role: 'patient', status: 'active' },
  ]);

  const [appointments, setAppointments] = useState([]);

  const [currentUser, setCurrentUser] = useState(null);

  const login = (user) => {
    // user: { role, name, email }
    // ensure user exists in users list if registered
    if (user.registered) {
      const exists = users.find(u => u.email === user.email);
      if (!exists) {
        const newUser = { id: users.length + 1, name: user.name, email: user.email, role: user.role || 'patient', status: 'active' };
        setUsers(prev => [...prev, newUser]);
        setCurrentUser(newUser);
        return;
      }
      setCurrentUser(exists);
      return;
    }
    // guest user object (not saved to users list)
    setCurrentUser(user);
  };

  const logout = () => setCurrentUser(null);

  const addAppointment = ({ doctorId, doctorName, specialty, date, patientName, patientEmail, patientRegistered, patientId }) => {
    const newAppointment = {
      id: appointments.length + 1,
      doctorId: doctorId || null,
      doctorName,
      specialty,
      date,
      patientName,
      patientEmail,
      patientRegistered: !!patientRegistered,
      patientId: patientId || null,
      status: 'Pending',
    };
    setAppointments(prev => [...prev, newAppointment]);
    return newAppointment;
  };

  const approveAppointment = (appointmentId) => {
    setAppointments(prev => prev.map(a => a.id === appointmentId ? { ...a, status: 'Approved' } : a));
  };

  const setAppointmentStatus = (appointmentId, status) => {
    setAppointments(prev => prev.map(a => a.id === appointmentId ? { ...a, status } : a));
  };

  const registerPatient = ({ name, email }) => {
    const exists = users.find(u => u.email === email);
    if (exists) return exists;
    const newUser = { id: users.length + 1, name, email, role: 'patient', status: 'active' };
    setUsers(prev => [...prev, newUser]);
    // update any appointments that match this email to be registered
    setAppointments(prev => prev.map(a => a.patientEmail === email ? { ...a, patientRegistered: true, patientId: newUser.id } : a));
    return newUser;
  };

  return (
    <AppContext.Provider value={{ users, appointments, currentUser, login, logout, addAppointment, approveAppointment, setAppointmentStatus, registerPatient, setUsers }}>
      {children}
    </AppContext.Provider>
  );
};

export const useAppContext = () => useContext(AppContext);

export default AppContext;
